import Sugestoes from "./Sugestoes";

let nomeDoUsuaio = "catanacomics";

function alterarNomeDoUsuario(){
  nomeDoUsuaio = prompt("Qual seu nome?");
}

function SideBar(){
    return(
        <div class="sidebar">
        <div class="usuario">
          <img src="assets/img/catanacomics.svg" alt="imagem de perfil"/>
          <div class="texto">
            <span>
              <strong>{nomeDoUsuaio}</strong>
              <button className="botao" onClick={alterarNomeDoUsuario}><ion-icon name="pencil"></ion-icon></button>
            </span>
          </div>
        </div>
        <Sugestoes />
        <div class="links">
          Sobre • Ajuda • Imprensa • API • Carreiras • Privacidade • Termos • Localizações • Contas mais relevantes •
          Hashtags • Idioma
        </div>

        <div class="copyright">
          © 2021 INSTAGRAM DO FACEBOOK
        </div>
      </div>
    )
}



export default SideBar;
